import React from "react";

import { Box } from "./Box";
import "./Board.css";

export const Board = ({ board,one_player, x_playing, game_over, onClick }) => {
  let next = ((is_one_player===true)&&(is_x_playing===false)&&(game_over ===false))?true:false;
  if( next === true) {
    onClick();
  }

  return (
    <div className="board">
      {board.map((value, idx) => {
        return (
          <Box
            value={value}
            onClick={() => {
              if(value === null)onClick(idx);
            }}
          />
        );
      })}
    </div>
  );
};
